/**
 * Product categories tab
 *
 * @category   Mage
 * @package    Mage_Adminhtml
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class IMI_Blog_Block_Adminhtml_Posts_Edit_Tab_Categories extends IMI_Blog_Block_Adminhtml_Category_Tree
{
    protected $_categoryIds;
    protected $_selectedNodes = null;

    public function __construct()
    {
        parent::__construct();
        $this->setTemplate('imi_blog/category/admintree.phtml');
    }
    public function getCategories(){
      return Mage::getModel('imi_blog/category')
        ->getCollection()
        ->addFieldToFilter('status', 1);
    }

}
